mapper.select:
SELECT p.id as Id, p.card_id as cardId, p.holder as holder, p.holder_id_no as holderIdNo, p.holder_phone as holderPhone, p.recognizee as recognizee, p.recognizee_id_no as recognizeeIdNo, p.recognizee_phone as recognizeePhone, p.start_time as startTime, p.end_time as endTime, p.export_status as exportStatus, p.create_time as createTime FROM policy p WHERE 1=1
AND p.export_status =#{exportStatus}
AND p.create_time >=#{createTimeBegin}
AND p.create_time <=#{createTimeEnd}
order by p.id desc

SELECT p.id as Id, p.card_id as cardId, c.card_no as cardNo, c.password as cardPassword, c.type as cardType, p.holder as holder, p.holder_id_no as holderIdNo, p.holder_address as holderAddress, p.holder_birthday as holderBirthday, p.holder_gender as holderGender, p.holder_occupation as holderOccupation, p.holder_phone as holderPhone, p.recognizee as recognizee, p.recognizee_id_no as recognizeeIdNo, p.recognizee_phone as recognizeePhone, p.holder_recognizee_relation as holderRecognizeeRelation, p.start_time as startTime, p.end_time as endTime, p.export_status as exportStatus, p.create_time as createTime FROM policy p,card c WHERE p.card_id = c.id
AND p.export_status =#{exportStatus}
AND p.create_time >=#{createTimeBegin}
AND p.create_time <=#{createTimeEnd}
AND c.type =#{cardType}
AND c.card_no >=#{startCardNo}
AND c.card_no <=#{endCardNo}
order by p.id desc

